package org.comit.course._02_operators;

public class _01_Arithmetic {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int a =8;
		int b =3;
		int c =5;
		int d =11;
		
		int sum = a + b; //Addition Operator
		int sub = a - b; //Subtraction Operator
		double div = (double)a / b; //Division Operator
		int mul = d * b; //Multiplication Operator
		int mod = c % b; //Division Remainder Operator
		
		System.out.println("Addition: "+sum);
		System.out.println("Subtraction: "+sub);
		System.out.println("Division: "+div);
		System.out.println("Multiplication: "+mul);
		System.out.println("Modulus: "+mod);
		
		String str ="Hello, "+"COmIT";
		System.out.println(str);
		

	}

}
